import React from 'react';
import { NavLink } from 'react-router-dom';
import { useSelector } from 'react-redux';

/**
 * Sidebar navigation component with links to main application sections
 */
const Sidebar = ({ closeSidebar }) => {
  const { currentScan } = useSelector(state => state.compliance);

  // Navigation items definition
  const navItems = [
    {
      path: '/',
      name: 'Dashboard',
      icon: (
        <svg className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6" />
        </svg>
      )
    },
    {
      path: '/controls',
      name: 'Controls',
      icon: (
        <svg className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2m-3 7h3m-3 4h3m-6-4h.01M9 16h.01" />
        </svg>
      )
    },
    {
      path: '/history',
      name: 'History',
      icon: (
        <svg className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
        </svg>
      )
    },
    {
      path: '/settings',
      name: 'Settings',
      icon: (
        <svg className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10.325 4.317c.426-1.756 2.924-1.756 3.35 0a1.724 1.724 0 002.573 1.066c1.543-.94 3.31.826 2.37 2.37a1.724 1.724 0 001.065 2.572c1.756.426 1.756 2.924 0 3.35a1.724 1.724 0 00-1.066 2.573c.94 1.543-.826 3.31-2.37 2.37a1.724 1.724 0 00-2.572 1.065c-.426 1.756-2.924 1.756-3.35 0a1.724 1.724 0 00-2.573-1.066c-1.543.94-3.31-.826-2.37-2.37a1.724 1.724 0 00-1.065-2.572c-1.756-.426-1.756-2.924 0-3.35a1.724 1.724 0 001.066-2.573c-.94-1.543.826-3.31 2.37-2.37.996.608 2.296.07 2.572-1.065z" />
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
        </svg>
      )
    }
  ];

  // Calculate compliance score and status
  const complianceScore = currentScan?.summary?.complianceScore || 0;
  const getComplianceStatus = (score) => {
    if (score >= 80) return { text: 'Good', color: 'bg-green-500' };
    if (score >= 60) return { text: 'Fair', color: 'bg-yellow-500' };
    return { text: 'Poor', color: 'bg-red-500' };
  };

  const complianceStatus = getComplianceStatus(complianceScore);

  return (
    <div className="flex flex-col h-full">
      {/* Logo and title */}
      <div className="flex items-center justify-center p-6 border-b border-blue-700">
        <svg className="h-8 w-8 mr-2" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
          <path d="M20.5 9.5l-9-5.25L2.5 9.5l9 5.25 9-5.25z" fill="currentColor" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" />
          <path d="M3 9.5v5.25l9 5.25 9-5.25V9.5" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" />
        </svg>
        <h1 className="text-xl font-bold">CIS Compliance</h1>
      </div>

      {/* Compliance score widget */}
      {currentScan && (
        <div className="p-4 bg-blue-900 text-center">
          <p className="text-sm text-blue-200 mb-1">Compliance Score</p>
          <div className="flex items-center justify-center">
            <div className="relative inline-flex items-center justify-center">
              <svg className="w-20 h-20">
                <circle 
                  className="text-blue-700" 
                  strokeWidth="4" 
                  stroke="currentColor" 
                  fill="transparent" 
                  r="30" 
                  cx="40" 
                  cy="40" 
                />
                <circle 
                  className={complianceStatus.color} 
                  strokeWidth="4" 
                  strokeDasharray={`${complianceScore * 1.88} 188.5`} 
                  strokeLinecap="round" 
                  stroke="currentColor" 
                  fill="transparent" 
                  r="30" 
                  cx="40" 
                  cy="40" 
                  transform="rotate(-90 40 40)" 
                />
              </svg>
              <span className="absolute text-xl font-bold">{complianceScore}%</span>
            </div>
          </div>
          <div className="flex justify-center mt-2">
            <span className={`px-2 py-1 text-xs rounded-full text-white ${complianceStatus.color}`}>
              {complianceStatus.text}
            </span>
          </div>
        </div>
      )}

      {/* Navigation links */}
      <nav className="flex-1 px-4 py-6">
        <ul className="space-y-2">
          {navItems.map((item) => (
            <li key={item.path}>
              <NavLink
                to={item.path}
                className={({ isActive }) => 
                  `flex items-center px-4 py-3 rounded-lg transition-colors ${
                    isActive 
                      ? 'bg-blue-700 text-white' 
                      : 'text-blue-100 hover:bg-blue-700 hover:text-white'
                  }`
                }
                onClick={() => closeSidebar()}
              >
                {item.icon}
                <span className="ml-3">{item.name}</span>
              </NavLink>
            </li>
          ))}
        </ul>
      </nav>

      {/* Bottom section - AWS Account info */}
      <div className="p-4 border-t border-blue-700 mt-auto">
        <div className="bg-blue-900 rounded-lg p-3">
          <p className="text-xs text-blue-300 font-semibold">AWS Account</p>
          <p className="text-sm text-white truncate">My Production Account</p>
          <p className="text-xs text-blue-300 mt-1 truncate">ID: 123456789012</p>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
